import React, { useState } from 'react';
import image1 from './images/image1.png';
import image2 from './images/image2.png';
import image3 from './images/image3.png';
import image4 from './images/image4.png';

const ImageGrid = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const images = [
    { image: image1, category: 'Category 1' },
    { image: image2, category: 'Category 2' },
    { image: image3, category: 'Category 3' },
    { image: image4, category: 'Category 4' },
  ];

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="grid-container">
      {images.map((iimgmage, index) => (
        <div key={index} className={`grid-item category-${index + 1}`}>
          <img src={image.image} alt={`Image ${index + 1}`} />
          <button onClick={() => handleCategorySelect(image.category)}>
            {image.category}
          </button>
          <div className="border"></div>
        </div>
      ))}
    </div>
  );
};

export default ImageGrid;
