import React, { useState } from 'react';
import image1 from '../assets/img/jacket.png';
import image2 from '../assets/img/diary_bg.png';
import image3 from '../assets/img/tshirt_bg.png';
import image4 from '../assets/img/mug_bg.png';

const ImageGrid = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const images = [image1, image2, image3, image4];

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="grid-container">
      {images.map((img, index) => (
        <div key={index} className="grid-item">
          <img src={img} alt={`Img ${index + 1}`} />
          <button onClick={() => handleCategorySelect(`Category ${index + 1}`)}>
            {`Category ${index + 1}`}
          </button>
        </div>
      ))}
    </div>
  );
};

export default ImageGrid;
