import React, { useState } from 'react';
import image1 from '../assets/img/jacket.png';
import image2 from '../assets/img/jacket.png';
import image3 from '../assets/img/jacket.png';
import image4 from '../assets/img/jacket.png';

const ImageGrid = () => {
    const [selectedCategory, setSelectedCategory] = useState(null);
  
    const images = [
      { id: 1, image: image1, category: 'Category 1' },
      { id: 2, image: image2, category: 'Category 2' },
      { id: 3, image: image3, category: 'Category 3' },
      { id: 4, image: image4, category: 'Category 4' },
    ];
  
    const handleCategorySelect = (category) => {
      setSelectedCategory(category);
    };
  
    return (
      <div className="grid-container">
        {images.map((img) => (
          <div key={img.id} className={`grid-item category-${img.id}`}>
            <img src={img.image} alt={`Image ${img.id}`} />
            <button onClick={() => handleCategorySelect(img.category)}>
              {img.category}
            </button>
            <div className="border"></div>
          </div>
        ))}
      </div>
    );
  };
  
  export default ImageGrid;
