import React, { useState } from 'react';
import image1 from '../assets/img/jacket.png';
import image2 from '../assets/img/jacket.png';
import image3 from '../assets/img/jacket.png';
import image4 from '../assets/img/jacket.png';

const ImageGrid = () => {
    const [selectedCategory, setSelectedCategory] = useState(null);
  
    const images = [
      { id: 1, image: image1, category: 'Category 1' },
      { id: 2, image: image2, category: 'Category 2' },
      { id: 3, image: image3, category: 'Category 3' },
      { id: 4, image: image4, category: 'Category 4' },
    ];
  
    const handleCategorySelect = (category) => {
      setSelectedCategory(category);
    };
  
    return (
        <>
        <div class="one">
        <img class="one-img" src={"./assets/2.png"} alt=""/>
        </div>
        <div class="two">
        <img class="cat-2" src="./assets/3.png" alt=""/>
        <img class="cat-3" src="./assets/5.png" alt=""/>
        </div>
        <div class="one">
        <img class="one-img" src="./assets/4.png" alt=""/>
        </div>
      </>
    );
  };
  
  export default ImageGrid;
